rule.example:

// OK
@Value.Immutable
@BuckStyleImmutable
interface AbstractFoo {

}

// Bad: uses public visibility
@Value.Immutable
@BuckStyleImmutable
public class AbstractBar {

}

// Bad: uses public visibility
@Value.Immutable
@BuckStyleImmutable
public interface AbstractBaz {

}


// OK
@Value.Immutable
@BuckStyleImmutable
interface AbstractFoo {

}

// Bad: name does not start with Abstract
@Value.Immutable
@BuckStyleImmutable
interface Bar {

}


// OK
import com.facebook.buck.log.Logger;
private static final Logger LOG = Logger.getLogger(Main.class);

// Bad
import java.util.logging.Logger;
private static final Logger LOG = Logger.getLogger(Main.class.getName());


// OK
class Foo {
  public void bar(String s) {
    return s.replace('\r', '\n');
  }
}

// Bad: using expensive regular expressions to replace single characters
class Foo {
  public void bar(String s) {
    return s.replace("\r", "\n");
  }
}
