    this.kernel = new Kernel(this);
    this.processes = {};
    this.metadata = [];
    this.categories = [];
    this.bounds = new base.Range();
    this.instantEvents = [];
    this.flowEvents = [];

    this.flowIntervalTree = new base.IntervalTree(
        function(s) { return s.start; },
        function(e) { return e.start; });

